import React, { Component } from 'react';
import ReactCardFlip from 'react-card-flip';
import subject1 from './res/subject1.png';
import subject2 from './res/subject2.png';
import subject3 from './res/subject3.png';
import './App.css';


export default class MajorWheel extends Component {

  constructor() {
    super();
    this.state = {
      isFlipped: false
    };
    this.handleClick = this.handleClick.bind(this);
  }

  CardFlip = () => {
    // const [isFlipped, setIsFlipped] = userState(false);
  }

  handleClick(e) {
    e.preventDefault();
    this.setState(prevState => ({ isFlipped: !prevState.isFlipped }));
  }

  render() {
    return (
      <div className="App">

        <ReactCardFlip isFlipped={this.state.isFlipped} flipDirection="horizontal">
          <div>
            <img src={subject3} className="App-logo" alt="logo" onClick={this.handleClick} />
          </div>
          <div>
            <img src={subject2} className="App-logo" alt="logo" onClick={this.handleClick}  />
          </div>
        </ReactCardFlip>
      </div>
    );
  }

}
