import React, { Component } from 'react';
import ReacrCardFlip from 'react-card-flip';
import subject1 from './res/subject1.png';
import subject2 from './res/subject2.png';
import subject3 from './res/subject3.png';
import './App.css';


export default class MajorWheel extends Component{

  constructor() {
    super();
    this.state = {
      isFlipped: false
    };
    this.handleClick = this.handleClick.bind(this);

  }

  CardFlip = () => {
    const [isFlipped, setIsFlipped] = userState(false);
  }

  handleClick = () => {
    setIsFlipped(!isFlipped);
  }

  render () {
    return (
      <div className="App">
        <header className="App-header">
          <div>
            <img src={subject1} className="App-logo" alt="logo" onClick={() => this.renderWheel('subject1')} />
          </div>
          <div>
            <img src={subject2} className="App-logo" alt="logo" onClick={() => this.renderWheel('subject2')} />
          </div>
          <div>
            <img src={subject3} className="App-logo" alt="logo" onClick={() => this.renderWheel('subject3')} />
          </div>
        </header>
      </div>
    );
  }

}
